import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import "./Assest/Style.css";
import emailjs from "emailjs-com";
import { useRef } from "react";

function StudentForm() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const form = useRef();

  /* ********************** start nova alert ********************** */

  const novaAlert = function ({
    icon = "",
    title = "",
    text = "",
    darkMode = false,
    showCancelButton = false,
    CancelButtonText = "NO",
    ConfirmButtonText = "Okay",
    dismissButton = true,
    input = false,
    inputPlaceholder = "",
  }) {
    let modal = document.createElement("div");
    modal.setAttribute("class", "nova-modal");
    document.body.append(modal);
    let alert = document.createElement("div");
    alert.setAttribute("class", "nova-alert");

    modal.appendChild(alert);
    var svg;

    if (darkMode == true) {
      alert.classList.add("nova-dark-mode");
    }

    if (icon == "success") {
      svg = `<svg class="circular green-stroke">
            <circle class="path" cx="75" cy="75" r="50" fill="none" stroke-width="5" stroke-miterlimit="10"/>
        </svg>
        <svg class="checkmark green-stroke">
            <g transform="matrix(0.79961,8.65821e-32,8.39584e-32,0.79961,-489.57,-205.679)">
                <path class="checkmark__check" fill="none" d="M616.306,283.025L634.087,300.805L673.361,261.53"/>
            </g>
        </svg>`;
    } else if (icon == "danger") {
      svg = `<svg class="circular red-stroke">
            <circle class="path" cx="75" cy="75" r="50" fill="none" stroke-width="5" stroke-miterlimit="10"/>
        </svg>
        <svg class="cross red-stroke">
            <g transform="matrix(0.79961,8.65821e-32,8.39584e-32,0.79961,-502.652,-204.518)">
                <path class="first-line" d="M634.087,300.805L673.361,261.53" fill="none"/>
            </g>
            <g transform="matrix(-1.28587e-16,-0.79961,0.79961,-1.28587e-16,-204.752,543.031)">
                <path class="second-line" d="M634.087,300.805L673.361,261.53"/>
            </g>
        </svg>`;
    } else if (icon == "warning") {
      svg = `<svg class="circular yellow-stroke">
            <circle class="path" cx="75" cy="75" r="50" fill="none" stroke-width="5" stroke-miterlimit="10"/>
        </svg>
        <svg class="alert-sign yellow-stroke">
            <g transform="matrix(1,0,0,1,-615.516,-257.346)">
                <g transform="matrix(0.56541,-0.56541,0.56541,0.56541,93.7153,495.69)">
                    <path class="line" d="M634.087,300.805L673.361,261.53" fill="none"/>
                </g>
                <g transform="matrix(2.27612,-2.46519e-32,0,2.27612,-792.339,-404.147)">
                    <circle class="dot" cx="621.52" cy="316.126" r="1.318" />
                </g>
            </g>
        </svg>`;
    } else {
      svg = "";
    }
    var icon_template = ` <div class="nova-icon">
       <div class="svg-box">
         ${svg}
       </div>
   </div>`;
    var title_and_text = `
   <h3 class="nova-title">
      ${title}
   </h3>
   <p class="nova-text">
    ${text}
   </p>
   `;

    if (showCancelButton == true) {
      var buttons = `
   <div class="nova-btns">
   <a class="accept">
      ${ConfirmButtonText}
   </a>
   <a class="reject">
    ${CancelButtonText}
   </a>
   </div>
   `;
    } else {
      var buttons = `
   <div class="nova-btns">
   <a class="accept">
    ${ConfirmButtonText}
   </a>
   </div>
   `;
    }
    if (dismissButton == true) {
      var dismissButton = `<a class="dismissButton">
   X
   </a>`;
    } else {
      var dismissButton = `<a class="dismissButton hidden">
   X
   </a>`;
    }

    if (input == true) {
      var __input = `<input class="nova-input-alert" placeholder='${inputPlaceholder}'>`;
    } else {
      var __input = "";
    }

    var $content =
      icon_template + title_and_text + __input + buttons + dismissButton;

    alert.innerHTML = $content;

    document.querySelector(
      ".nova-alert .reject  , .nova-alert .accept"
    ).onclick = closeNova;
    document.querySelector(".dismissButton").onclick = closeNova;

    function closeNova() {
      alert.remove();
      modal.remove();
    }

    this.then = function (callback) {
      document.querySelector(".nova-alert .accept").onclick = accept;

      function accept() {
        if (input == true) {
          var inputValue = document.querySelector(".nova-input-alert");
          var val = inputValue.value;
          closeNova();
          callback((e = true), val);
        } else {
          closeNova();
          callback((e = true));
        }
      }

      document.querySelector(".nova-alert .reject").onclick = reject;
      function reject() {
        closeNova();
        callback((e = false));
      }
    };
  };

  /* ********************** end nova alert ********************** */

  new novaAlert({
    title: "Welcome",
    text: "Nova is a small JavaScript plugin for creating custom alert",
    icon: "success",
    dismissButton: false,
    darkMode: false,
    ConfirmButtonText: "Lets go",
  });

  function show_success() {
    new novaAlert({
      icon: "success",
      title: "Your Mail Has Sent Successfully",
      text: "Thanks for Registering",
      showCancelButton: true,
      CancelButtonText: "Close",
      ConfirmButtonText: "Ok",
    }).then((result) => {
      if (result) {
        new novaAlert({
          title: "you selected : yes i love this :)",
          icon: "success",
          text: "i know you liked this",
        });
      } else {
        new novaAlert({
          title: "you selected : no i hate this :(",
          icon: "danger",
          text: "why you hate me ? :(",
        });
      }
    });
  }

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_uskafun",
        "template_t450vuo",
        form.current,
        "ivKItVln0cZrmho0T"
      )
      .then(
        (result) => {
          console.log("Success", result.text);
        },
        (error) => {
          console.log("Error", error.text);
        }
      );

    handleClose();
  };

  return (
    <>
      <Button
        variant="light"
        className="item2 view-more rounded-pill navbar-btn"
        onClick={handleShow}
      >
        Register
      </Button>

      <Modal show={show} onHide={handleClose} className="modal">
        <Modal.Title className="heading">
          <Modal.Header closeButton className="heading">
            Registration Form
          </Modal.Header>
          <button onclick="show_success()">show success alert </button>
        </Modal.Title>
        <Modal.Body className="mBody">
          <Form className="sForm" ref={form} onSubmit={sendEmail}>
            <Form.Group className="mb-2" controlid="formBasicName">
              <Form.Control
                type="text"
                name="name"
                placeholder="Enter Name"
                required
                className="input-form rounded-pill"
              />
            </Form.Group>
            <Form.Group className="mb-2" controlid="formBasicEmail">
              <Form.Control
                type="email"
                name="email"
                placeholder="Enter email"
                required
                className="input-form rounded-pill"
              />
            </Form.Group>
            <Form.Group className="mb-2" controlid="formBasicNumber">
              <Form.Control
                type="number"
                name="phone"
                placeholder="PhoneNumber"
                required
                className="input-form rounded-pill"
              />
            </Form.Group>

            <Form.Group className="mb-2" controlid="formBasicDegree">
              <Form.Control
                type="text"
                name="degree"
                placeholder="Degree and Department"
                required
                className="input-form rounded-pill"
              />
            </Form.Group>
            <Form.Group className="mb-2" controlid="formBasicDegree">
              <Form.Control
                type="number"
                name="year"
                placeholder="Year of Studying"
                min="1"
                max="4"
                required
                className="input-form rounded-pill"
              />
            </Form.Group>
            <Form.Group className="btn-grp">
              <Button
                type="submit"
                className="close mt-3 butn submit-btn rounded-pill"
              >
                Submit
              </Button>
            </Form.Group>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default StudentForm;
