import React from "react";
import ReactImg from "../Images/ReactJS.png";
import "./Assest/TabsStyle.css";
let data = [
  {
    name: "Overview",
    text: (
      <p className="course-detail">
        <h4>Declarative</h4>
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;React makes it painless to create
        interactive UIs. Design simple views for each state in your application,
        and React will efficiently update and render just the right components
        when your data changes. Declarative views make your code more
        predictable and easier to debug.
        <h4>Component-Based</h4>
        Build encapsulated components that manage their own state, then compose
        them to make complex UIs. Since component logic is written in JavaScript
        instead of templates, you can easily pass rich data through your app and
        keep state out of the DOM.
        <h4>Learn Once, Write Anywhere</h4>
        So you can develop new features in React without rewriting existing
        code. React can also render on the server using Node and power mobile
        apps using React Native.
      </p>
    ),
  },
  {
    name: "Course Details",
    text: (
      <p className="course-detail">
        <div className="card topics">
          <div className="card-body">
            <h5 className="card-title title5">Course Details</h5>
            <ul className="details-list">
              <li>
                <div className="row">
                  <div className="coursename col-2">Course Duration:</div>
                  <div className="courseDetails col">2 weeks</div>
                </div>
              </li>
              <br />
              <li>
                <div className="row">
                  <div className="coursename col-2">Topics Covered :</div>
                  <div className="courseDetails col">
                    <br />
                    React Introduction
                    <br />
                    Components
                    <br />
                    CSS React
                    <br />
                    State & Props.
                    <br />
                    Component Life Cycle
                    <br />
                    React Forms
                    <br />
                    List,Keys,Fragement.
                    <br />
                    Hooks
                    <br />
                    Router
                  </div>
                </div>
              </li>
              <br />
              <li> Conducting Daily Tasks for Success</li>
              <br />
              <li>Every weekends mock will be conducted</li>
            </ul>
          </div>
        </div>
      </p>
    ),
  },
];

class Tab6 extends React.Component {
  constructor() {
    super();

    this.state = {
      activeTab: 0,
      data: data,
    };

    this.changeTabOnClick = this.changeTabOnClick.bind(this);
  }

  changeTabOnClick(index) {
    this.setState({
      activeTab: index,
    });
  }

  render() {
    return (
      <div className="tabs-body">
        <TabHeader
          data={this.state.data}
          click={this.changeTabOnClick}
          activeId={this.state.activeTab}
        />
        <TabContent data={this.state.data} activeId={this.state.activeTab} />
      </div>
    );
  }
}

class TabHeader extends React.Component {
  doClick(index, event) {
    this.props.click(index);
  }

  render() {
    let activeClass = this.props.activeId;

    let tabs = this.props.data.map((item, index) => {
      return (
        <li className={activeClass === index ? "active" : ""}>
          <a onClick={this.doClick.bind(this, index)}>
            <span>{item.name}</span>
          </a>
        </li>
      );
    });

    return <ul className="tabs-header">{tabs}</ul>;
  }
}

class TabContent extends React.Component {
  render() {
    let activeClass = this.props.activeId;

    let content = this.props.data.map((item, index) => {
      return (
        <div
          className={"tabs-textItem " + (activeClass === index ? "show" : "")}
        >
          <p>{item.text}</p>
        </div>
      );
    });

    return (
      <div className="tabs-content">
        <div className="row">
          <div className="col-5 course-img-holder">
            <img src={ReactImg} alt="" />
          </div>
          <div className="col-7">{content}</div>
        </div>
      </div>
    );
  }
}

export default Tab6;
