// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/effect-fade";
import "swiper/css/navigation";
import "swiper/css/pagination";
import Aws from "../Images/cloud-services.jpg";
import Testing from "../Images/software-testing-image.jpg";
import Development from "../Images/aws.jpg";

// import required modules
import { EffectFade, Autoplay, Navigation, Pagination } from "swiper";

export default function LandingPage() {
  return (
    <>
      <Swiper
        spaceBetween={30}
        effect={"fade"}
        navigation={true}
        autoplay={{
          delay: 2000,
          disableOnInteraction: false,
        }}
        pagination={{
          clickable: true,
        }}
        modules={[EffectFade, Navigation, Pagination, Autoplay]}
        className="mySwiper"
      >
        <SwiperSlide className="slide1 slides">
          <div className="row landing-row">
            <div className="col-5"></div>
            <div className="col-7 landing-text">
              <div className="text-hold">
                Career Guidance Technology is a software training institute
                provides education and training in various software technologies
                to prepare individuals for careers in the tech industry. We
                offer courses in programming languages, web development,
                software testing, database management, and more. We also provide
                career guidance and placement assistance to help students secure
                jobs in the industry.
              </div>
            </div>
            <button className="view-more rounded-pill">
              Click to view more
            </button>
          </div>
        </SwiperSlide>
      </Swiper>
    </>
  );
}
